mapper.select:
select id, sensor_id, original_data, data, create_time from wxcxc_project_device_sensor_data_${deviceId}

    and id = #{wxcxcProjectDeviceSensorData.id}
    and sensor_id = #{wxcxcProjectDeviceSensorData.sensorId}
    and original_data = #{wxcxcProjectDeviceSensorData.originalData}
    and data = #{wxcxcProjectDeviceSensorData.data}
    and create_time = #{wxcxcProjectDeviceSensorData.createTime}

        AND date_format(create_time,'%Y%m%d%H%i') >= date_format(#{wxcxcProjectDeviceSensorData.params.beginTime},'%Y%m%d%H%i')


        and date_format(create_time,'%Y%m%d%H%i') <= date_format(#{wxcxcProjectDeviceSensorData.params.endTime},'%Y%m%d%H%i')

mapper.insert:
insert into wxcxc_project_device_sensor_data_${deviceId}
sensor_id, original_data, data, create_time,
#{wxcxcProjectDeviceSensorData.sensorId}, #{wxcxcProjectDeviceSensorData.originalData}, #{wxcxcProjectDeviceSensorData.data}, #{wxcxcProjectDeviceSensorData.createTime},
