mapper.select:
select id, point_id, data, create_time from wxcxc_project_point_data_${structureId}

    and id = #{wxcxcProjectPointData.id}
    and point_id = #{wxcxcProjectPointData.pointId}
    and data = #{wxcxcProjectPointData.data}
    and create_time = #{wxcxcProjectPointData.createTime}


        AND date_format(create_time,'%Y%m%d%H%i') >=
        date_format(#{wxcxcProjectPointData.params.beginTime},'%Y%m%d%H%i')


        and date_format(create_time,'%Y%m%d%H%i') <=
        date_format(#{wxcxcProjectPointData.params.endTime},'%Y%m%d%H%i')

mapper.insert:
insert into wxcxc_project_point_data_${structureId}
point_id, data, create_time,
#{wxcxcProjectPointData.point_id}, #{wxcxcProjectPointData.data}, #{wxcxcProjectPointData.createTime},

mapper.select:
select id, point_id, data, create_time from wxcxc_project_point_data_${structureId}
and point_id = #{pointId}
AND date_format(create_time,'%Y%m%d%H%i') >= date_format(#{beginTime},'%Y%m%d%H%i')
and date_format(create_time,'%Y%m%d%H%i') <= date_format(#{endTime},'%Y%m%d%H%i')
ORDER BY create_time DESC limit #{position},1
